<template>
  <div> 
    <div class="container" style="backgroundColor: #2E2954;">
      <sp-h3 v-if="type==='lottery'" style="color: #FFFFFF;">Create your own lottery</sp-h3>
      <sp-h3 v-else-if="type==='ticket'" style="color: #FFFFFF;">Buy a ticket</sp-h3>
      <sp-h3 v-else-if="type==='prizeAnnounce'" style="color: #FFFFFF;">Announce lottery result</sp-h3>
      <sp-h3 v-else style="color: #FFFFFF;">New {{ type }}</sp-h3>
      
      <div v-for="field in fields" :key="field" style="color: #FFFFFF;">
        <div v-if="title(field)==='Drawdate'" class="wrapper-class">
            <label style="display: inline; color: rgba(0, 0, 0, 0.43);">Draw Date </label>
            <datetime type="datetime" v-model="fieldsList[field]" class="input-style theme-purple" placeholder="  Choose a date"></datetime>
            <!-- <datepicker v-model="fieldsList[field]" class="calendar-class input-class" placeholder="  Choose a date"></datepicker> -->
        </div>
        <sp-input
          v-else
          v-model="fieldsList[field]"
          type="text"
          :placeholder="title(field)"
          :disabled="flight"
        />
      </div>
      
      <div class="button">
        <sp-button
          :loading="flight"
          :disabled="!valid || !hasAddress || flight"
          @click="submit"
          style="color: #FFFFFF;"
          v-if="type==='lottery'"
        >
          Create lottery
        </sp-button>
        <sp-button
          :loading="flight"
          :disabled="!valid || !hasAddress || flight"
          @click="submit"
          style="color: #FFFFFF;"
          v-else-if="type==='ticket'"
        >
          Buy ticket
        </sp-button>
        <sp-button
          :loading="flight"
          :disabled="!valid || !hasAddress || flight"
          @click="submit"
          style="color: #FFFFFF;"
          v-else-if="type==='prizeAnnounce'"
        >
          Announce
        </sp-button>
        <sp-button
          :loading="flight"
          :disabled="!valid || !hasAddress || flight"
          @click="submit"
          style="color: #FFFFFF;"
          v-else
        >
          Create {{ type }}
        </sp-button>
      </div>
      <!-- <sp-h3 style="color: #FFFFFF;"
        >List of {{ type }} items {{ module && `in module ${module}` }}</sp-h3
      > -->
      <sp-h3 v-if="type==='lottery'" style="color: #FFFFFF;">List of lotteries</sp-h3>
      <sp-h3 v-else-if="type==='ticket'" style="color: #FFFFFF;">List of tickets</sp-h3>
      <sp-h3 v-else-if="type==='prizeAnnounce'" style="color: #FFFFFF;">List of prize announcements</sp-h3>
      <sp-h3 v-else style="color: #FFFFFF;">List of {{ type }} items</sp-h3>

      <div class="item" v-for="instance in instanceList" :key="instance.id">
        <div class="item__field" v-for="(value, key) in instance" :key="key">
          <div class="item__field__key" style="color: #FFFFFF;">{{ key }}:</div>
          <div v-if="key === 'reward' || key === 'price'" class="item__field__value" style="color: #FFFFFF;">
            {{ value[0].amount }} {{ value[0].denom }}
          </div>
          <!-- <div v-else-if="type==='prizeAnnounce' && key==='ticketID' && value.length<1" class="item__field__value" style="color: #FFFFFF;">
            None
          </div> -->
          <!-- <div v-else-if="type==='prizeAnnounce' && key==='finalReward' && value===''" class="item__field__value" style="color: #FFFFFF;">
            0 token
          </div> -->
          <div v-else class="item__field__value" style="color: #FFFFFF;">
            {{ value }}
          </div>
        </div>
      </div>
      <div v-if="type==='lottery' && instanceList.length < 1" class="card__empty" style="color: #FFFFFF;">There are no lotteries yet.</div>
      <div v-else-if="type==='ticket'&& instanceList.length < 1" class="card__empty" style="color: #FFFFFF;">There are no tickets yet.</div>
      <div v-else-if="type==='prizeAnnounce'&& instanceList.length < 1" class="card__empty" style="color: #FFFFFF;">There are no prize announcements yet.</div>
      <div v-else-if="instanceList.length < 1" class="card__empty" style="color: #FFFFFF;">There are no {{ type }} items yet.</div>
    </div>
  </div>
</template>

<style scoped>
@import url("https://fonts.googleapis.com/css2?family=Inter:wght@100;200;300;400;500;531;600;700;800&display=swap");

